INSERT INTO settings (key, value) VALUES
('email_template_workflow_created_subject', 'Płatność przyjęta — czas na podpisanie umowy'),
('email_template_workflow_created_body', '<!DOCTYPE html><html><head><meta charset="utf-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"></head><body style="margin:0;padding:0;font-family:-apple-system,BlinkMacSystemFont,''Segoe UI'',Roboto,''Helvetica Neue'',Arial,sans-serif;background-color:#000000;"><table width="100%" cellpadding="0" cellspacing="0" style="background-color:#000000;padding:40px 20px;"><tr><td align="center"><table width="560" cellpadding="0" cellspacing="0" style="background-color:#0a0a0a;border-radius:12px;border:1px solid #262626;"><tr><td style="padding:48px 40px 40px 40px;"><p style="margin:0 0 8px 0;color:#10b981;font-size:12px;font-weight:600;text-transform:uppercase;letter-spacing:1px;">Płatność przyjęta</p><h1 style="margin:0 0 24px 0;color:#ffffff;font-size:28px;font-weight:600;line-height:1.3;">{{clientName}}, płatność dotarła!</h1><p style="margin:0 0 32px 0;color:#a3a3a3;font-size:15px;line-height:1.6;">Otrzymałem płatność za <span style="color:#ffffff;">{{offerName}}</span>. Teraz zaloguj się do panelu klienta, żebyśmy mogli podpisać umowę i ruszyć z projektem.</p><table width="100%" cellpadding="0" cellspacing="0" style="margin-bottom:24px;"><tr><td align="center"><a href="{{projectUrl}}" style="display:inline-block;background:linear-gradient(135deg,#10b981 0%,#059669 100%);color:#ffffff;text-decoration:none;padding:16px 32px;border-radius:8px;font-size:14px;font-weight:600;text-align:center;">Przejdź do panelu →</a></td></tr></table><p style="margin:0;color:#737373;font-size:13px;line-height:1.5;">Po zalogowaniu wypełnij dane do umowy — resztą zajmę się ja.</p></td></tr><tr><td style="padding:20px 40px;border-top:1px solid #262626;text-align:center;"><a href="https://tomekniedzwiecki.pl" style="color:#525252;font-size:12px;text-decoration:none;">tomekniedzwiecki.pl</a></td></tr></table></td></tr></table></body></html>')
